query:
mutation QuantityIncrease($offerId: String!, $quantity: Int!) {
  increaseItemQuantity(
    increaseItemQuantityInput: {item: {offerId: $offerId, quantity: $quantity}}
  ) {
    offers {
      offerGroups {
        offers {
          offer {
            offerRef {
              ... on Product {
                id
                baseInformation {
                  name
                  categoryInfo {
                    categories {
                      name
                      url
                      __typename
                    }
                    __typename
                  }
                  currentVariant {
                    name
                    __typename
                  }
                  smartAuthor {
                    name
                    link
                    __typename
                  }
                  selfUrl
                  cover {
                    small
                    __typename
                  }
                  __typename
                }
                energyEfficiency {
                  newEnergyClass
                  energyClass
                  energyLabelUrl
                  productCardUrl
                  __typename
                }
                detailsInformation {
                  digitalFormat
                  __typename
                }
                __typename
              }
              ... on SubscriptionInfo {
                group
                definitionId
                name
                priceVariant {
                  id
                  name
                  price
                  billingPeriods
                  chargeQuantity
                  offerId
                  __typename
                }
                __typename
              }
              __typename
            }
            originalPrice
            appliedDiscount {
              price
              discountPercentage
              discountType
              __typename
            }
            offerId
            merchant {
              id
              name
              url
              __typename
            }
            vatMargin
            quantity
            availability {
              name
              type
              __typename
            }
            compoundId
            unitPrice
            state
            description
            __typename
          }
          type
          maxQuantity
          availableQuantity
          dispatchAvailability {
            name
            type
            __typename
          }
          __typename
        }
        compounds {
          compoundId
          name
          appliedDiscount {
            discountAmount
            __typename
          }
          __typename
        }
        summary {
          minimalDeliveryAmount
          __typename
        }
        delivery {
          storeDelivery {
            cost
            __typename
          }
          minimalDelivery {
            cost
            __typename
          }
          possibleDiscount {
            promotionType
            missingValue
            threshold
            __typename
          }
          __typename
        }
        __typename
      }
      summary {
        totalAmount
        offersAmount
        minimalDeliveryAmount
        productCount
        subscriptionsCount
        discountPercentage
        discountAmount
        originalOffersAmount
        originalTotalAmount
        subscriptionsAmount
        __typename
      }
      premiumSummary {
        totalAmount
        offersAmount
        __typename
      }
      summaryWithoutPremium {
        totalAmount
        offersAmount
        __typename
      }
      couponCode
      couponValidationMessage
      potentialPromotionInfo {
        campaignId
        offersNumberToApplyPromotion
        highlight
        productIdsFromPromotion
        __typename
      }
      __typename
    }
    __typename
  }
}
